import React, { useState } from 'react';
import { addBusinessTrip } from '../services/api';
import TextInput from './TextInput';
import SelectInput from './SelectInput';

// Komponente zum Hinzufügen von Geschäftsreisen
const AddBusinessTrip = () => {
  const [destination, setDestination] = useState('');
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [date, setDate] = useState('');
  const [error, setError] = useState('');

  const destinations = ["New York", "San Francisco", "Chicago", "Los Angeles", "Miami"];

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!destination || !name || !email || !date) {
      setError('Bitte alle Felder ausfüllen');
      return;
    }
    const newTrip = { destination, name, email, date };
    addBusinessTrip(newTrip).then(() => {
      setDestination('');
      setName('');
      setEmail('');
      setDate('');
      setError('');
    });
  };

  return (
    <form onSubmit={handleSubmit}>
      <SelectInput
        value={destination}
        onChange={(e) => setDestination(e.target.value)}
        options={destinations}
        placeholder="Select Destination"
      />
      <TextInput
        type="text"
        value={name}
        onChange={(e) => setName(e.target.value)}
        placeholder="Name"
      />
      <TextInput
        type="email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
        placeholder="Email"
      />
      <TextInput
        type="date"
        value={date}
        onChange={(e) => setDate(e.target.value)}
        placeholder="Date"
      />
      {error && <p style={{ color: 'red' }}>{error}</p>}
      <button type="submit">Add Trip</button>
    </form>
  );
};

export default AddBusinessTrip;
